// SPDX-License-Identifier: MIT
pragma solidity >=0.5.3 <0.9.0;

contract Betting {
    address public owner;
    uint256 public minimumBet;
    uint256 public totalBetsOne;
    uint256 public totalBetsTwo;
    address []public players;

    struct Player {
        uint256 amountBet;
        uint16 teamSelected;
    }
    // The address of the player and => the user info
    mapping(address => Player) public playerInfo;

    fallback() external payable {
        // custom function code
    }

    receive() external payable {
        // custom function code
    }

    constructor () {
        owner = msg.sender;
        minimumBet = 100000000000000;
    }

    function kill() public {
        // if (msg.sender == owner) selfdestruct(msg.sender);
    }

    function checkPlayerExists(address player) public view returns (bool){
        for (uint256 i = 0; i < players.length; i++) {
            if (players[i] == player) return true;
        }
        return false;
    }


    function bet(uint8 _teamSelected) public payable {
        //The first require is used to check if the player already exist
        require(!checkPlayerExists(msg.sender));
        //The second one is used to see if the value sended by the player is
        //Higher than the minimum value
        require(msg.value >= minimumBet);

        //We set the player info : amount of the bet and selected team
        playerInfo[msg.sender].amountBet = msg.value;
        playerInfo[msg.sender].teamSelected = _teamSelected;

        //then we add the address of the player to the players array
        players.push(payable(msg.sender));

        //at the end, we increment the stakes of the team selected with the player bet
        if (_teamSelected == 1) {
            totalBetsOne += msg.value;
        }
        else {
            totalBetsTwo += msg.value;
        }
    }
    // Generates a number between 1 and 10 that will be the winner
    function distributePrizes(uint16 teamWinner) public {
        address [1000] memory winners;
        //We have to create a temporary in memory array with fixed size
        //Let's choose 1000
        uint256 count = 0;
        // This is the count for the array of winners
        uint256 LoserBet = 0;
        //This will take the value of all losers bet
        uint256 WinnerBet = 0;
        //This will take the value of all winners bet
        address add;
        address playerAddress;

        //We loop through the player array to check who selected the winner team
        for (uint256 i = 0; i < players.length; i++) {
            address tempPlayerAddress = players[i];

            //If the player selected the winner team
            //We add his address to the winners array
            if (playerInfo[tempPlayerAddress].teamSelected == teamWinner) {
                winners[count] = tempPlayerAddress;
                count++;
            }
        }

        //We define which bet sum is the Loser one and which one is the winner
        if (teamWinner == 1) {
            LoserBet = totalBetsTwo;
            WinnerBet = totalBetsOne;
        }
        else {
            LoserBet = totalBetsOne;
            WinnerBet = totalBetsTwo;
        }


        //We loop through the array of winners, to give ethers to the winners
        // TODO: 利益分配
        for (uint256 j = 0; j < count; j++) {
            // Check that the address in this fixed array is not empty
            if (winners[j] != address(0)) {
                add = winners[j];
                uint256 tempBet = playerInfo[add].amountBet;
                //Transfer the money to the user
                // address(uint160(winners[j])).transfer((tempBet * (10000 + (LoserBet * 10000 / WinnerBet))) / 10000);
                payable(winners[j]).transfer(tempBet * (LoserBet / WinnerBet));
            }
        }
        // Delete all the players
        delete playerInfo[playerAddress];
        // Delete all the players array
        delete players;
        // players.length = 0;
        LoserBet = 0;
        //reinitialize the bets
        WinnerBet = 0;
        totalBetsOne = 0;
        totalBetsTwo = 0;
    }

    function AmountOne() public view returns (uint256){
        return totalBetsOne;
    }

    function AmountTwo() public view returns (uint256){
        return totalBetsTwo;
    }
}
